select id,users.open_id, campus_id,subscribe,nickname, headimg_url, language,province,groupid,country, city,sex,remark, total_amount, subscribe_time, phone, address as default_address from users left join usersinfo on users.open_id = usersinfo.open_id
users.campus_id=#{campusId,jdbcType=INTEGER}
and (nickname like #{search,jdbcType=VARCHAR} or usersinfo.phone like #{search,jdbcType=VARCHAR} or usersinfo.address like #{search,jdbcType=VARCHAR})
order by ${sort}
${order}
limit #{offset,jdbcType=INTEGER} , #{limit,jdbcType=INTEGER}

